function area = bvncdf(x,y,ux,uy,s2x,s2y,r)% bvncdf(x,y) computes the cumulative bivariate normal distribution: the% area for values from -inf to x and y.% ux, uy are means% s2x, s2y are variances% r is the r value% 4/5/99 mns wrote itglobal Cglobal musx = sqrt(s2x);sy = sqrt(s2y);C = [s2x r*sx*sy; r*sx*sy s2y];mu = [ux uy]';area = dblquad('bvnpdf',ux-5*sx,x,uy-5*sy,y);