function p = bvnpdfYgtX(x,y)% function p = bvnpdfYgtX(x,y) computes the bivariate normal prob density% function at point x,y but is zero unless y>x.% This function is written so as to interface% with dblquad for numerical integration.  That means that the inner% variable can be a vector (x).%% The means and covariance are passed as global parameters. % See mvnpdf for the general version of the multivariate normal pdf.% 7/7/99 mns wrote itglobal muglobal Cp = repmat(0,size(x));for i = 1:length(x)  z = [x(i) y]';  dim = 2;  if y>x(i)    p(i) = ((2*pi)^(-.5*dim) / sqrt(det(C))) ...	   * exp(-.5 * (z - mu)' * inv(C) *  (z - mu))';  else    p(i) = 0;  endend    