function defineEcodes(paranum)%  defineEcodes(paranum)  fills in the values for all the ecode terms%        These are defined as global type variables in globalEcodeHdr.%        the input is the paradigm number.  That is a need we anticipate but%        we're not sure exactly how we'll implement it in the long run.%        Conceivably, some paradigms may have their own special ecodes.% 10/22/96 mil & mns wrote it% get the global defs.globalEcodeHdr;% fill in the numbers GORANDCD = 4900;	% turn on dots GOCOHCD = 4901;		%  turn on coherence  ENDCD = 4902;		% turn off dots  ALLONCD = 4903; ALLOFFCD = 4904; CORRECTCD = 4905; WRONGCD = 4906; NOCHCD = 4907; REW1CD = 4908; LOOPCD = 4909; PREFCD	= 4910; NULLCD	= 4911; askoffcd = 4912; FIX1CD = 4913;   % Matt's for first entry into fix window (para 512) BIGREWCD = 4914; % I must make sure to enter this in the lcode.h file. CHGFPCD = 4915;			% Jamie's 602 blue change % index codes that specify the meaning of the next 7000 ECODEHEADCD = 8000;	 % marks beginning of a series	         % and should be followed by a series                 % of index 8000+n ECODE pairs.		  I_FIXXCD = 8001;	% fix x  I_FIXYCD = 8002;	% fix y  I_STXCD = 8003;         % stim center x I_STYCD = 8004;	        % stim center y  I_STDIACD = 8005;	% stim (aperture) diameter I_SPDCD = 8006;	        % speed I_VNVCD = 8007;	        % var novar I_TRG1XCD = 8008;	% target iposition X I_TRG1YCD = 8009;	% targ position Y I_DOTDIRCD = 8010;      % pref dir I_COHCD = 8011;	        % correlation I_TRG2XCD = 8012;	% target 2 X I_TRG2YCD = 8013;       % target 2 Y I_SEEDCD = 8014;	% seed range CHMADECD = 8015;	% code to indicate choice was made EYEHLDCD = 8016;	% code to indicate eye was held in wind. I_TRIALIDCD= 8017;	% trial id number I_STIMDURCD = 8018;	% duration id; new to 502 FAILCD	= 8191; % Event codes specific per laboratory.  FPONCD	= 1010; FPONBLINKCD = 1012; FPOFFBLINKCD = 1014; FPDIMCD = 1020; FPOFFCD = 1025; REWCD = 1030; STIMCD	= 1100; STOFFCD = 1101; STONBLINKCD = 1120;	% stim blink  STOFFBLINKCD = 1122;	% stim blink off  PHIONCD = 1125; 	% phi blink on  PHISTARTCD = 1127;	% phi sequence started  PHIENDCD = 1129;	% phi sequence ended  BASECD	= 1500;	        % Code that can be modified by an			% action return.  Leave with 300 consecutive		        %  unused numbers.    STBONCD = 2048; STBOFFCD = 2064;  ANSWERCD = 2100;%  when the mirror is flicked to blank the image, this code goes out  BLANKCD = 2150;% local paradigm EVENT codes  TARGONCD = 3000; TARGOFFCD = 3002; TARGON1CD = 3100; TARGON2CD = 3101; TARG1OFF = 3102; TARG2OFF = 3103; BRANCHCD = 2300; ONSTABCD = 2100; ONSLIPCD = 2400; ONSTPCD = 2500; ONRMPCD = 2600; OFFSTABC = 2101; DARK = 2700; OKNSTART = 2702; OKNSAT = 2703; OKAN = 2704;% defines for rampflags  RS_ENDLST = 01; RS_CDONE = 02; RS_ADONE = 04; RS_MONITOR = 010; RS_TDONE = 020; RS_SAC	= 040; RS_STAB = 0100; RS_OFF	= 0200; RS_SLIP = 0400; RS_STP	= 01000; RS_RMP	= 02000; RS_RAMP = 04000;% Paradigm Ecodes  EYINWD = 1006; SACMAD = 1007; PURSCD = 1008; BCKDRK = 1026; BCKLIT = 1027; TGBLCD = 1028; TGBLOF = 1029; TARGON = 1040; TARGOF = 1041; STABON = 1050; STABOF = 1051; TTGBLC = 1052; TSTABO = 1053; TSTIMC = 1054; TTRGON = 1055; TFPNBK = 1056; TVDIS = 1057; TVOFF = 1058; ELESTM = 1059; TVACC = 1060; BCKNOIS = 1061; TELESTM = 1062; ELEOFF = 1063; %	System wide ecode defines.  Defines specific per laboratory are % located in .. lhdr lcode.h. % %	For REX 1.1 the following ecode mappings exist: % ecode < 0:	Event indexes an entry in A file. (e.g. eye movement) % ecode > 0:	Event is simple epoch. (e.g. unit firing) %	Bits 14 and 13 are special bits used as masks. % In addition, all events with absolute value less than 100 are % reserved for system use.  Therefore, the permissable range % of event codes currently is: %	-101 to -8192 for events that index A file %	 101 to  8191 for events that are simple epochs %	Each investigator should place non-standard ecodes specific % for his use in a conditionally compiled section. %  % %----------------------------------------------------------------------- %		System constants;  these are not ecodes. %----------------------------------------------------------------------- %   INIT_MASK = 020000;  CANCEL_MASK = 040000;  YES = 1;	 % arguments for score function   NO = 0;  OPEN_W = 0;	 % arguments for setdata function-		 % manipulates analog window    CLOSE_W = 1;  CANCEL_W = 2;  EYEH = 0400;	 % mask to test eyeflag bits;  if bit is	         % set eye is outside window    EYEV = 01;  EYEB = 0401;  %----------------------------------------------------------------------- %		System Codes:  Don't Change %-----------------------------------------------------------------------   STDHDRCD = -1;	 % standard header code  MESGCD = -2;	 % code for an ASCII string message  SACONHCD = 1;	 % horiz saccade onset  SACONVCD = 2;  ACTRETURN = 5;	 % non-zero code used when ecode is returned		 % from action   ESTOPCD = 10;	 % someone stopped the event writing out  %----------------------------------------------------------------------- %		Negative Codes:  Index A file %-----------------------------------------------------------------------    EYEHCD = -110;	 % horizontal eye  EYEVCD = -111;	 % vertical eye   MIRHCD = -200;	 % horizontal mirror  MIRVCD = -201;	 % vertical mirror   EMGCD = -301;	 % emg channel  %-----------------------------------------------------------------------% %		Positive Codes:  Simple Epochs %-----------------------------------------------------------------------%    %	Spot calibration codes.   TARGETCD = 510; % target jump: location and time  EHCALCD = 520;	 % calibration point for horizontal eye  EVCALCD = 521;	 % calibration point for vertical eye  %	Single unit codes.   spikecd = 601;             UNIT1CD = 601;	 % channel 1 units   UNIT2CD = 602;	 % channel 2 units    UNIT3CD = 603;	 % channel 3 units    UNIT4CD = 604;	 % channel 4 units    UNIT5CD = 605;	 % channel 5 units    UNIT6CD = 606;	 % channel 6 units   UNIT7CD = 607;	 % channel 7 units  %	Window manipulation codes.   WOPENCD = 800;  WCLOSECD = 801;  WCANCELCD = 802;  WERRCD = 803;  UWOPENCD = 804;	 % unit window %   UWCLOSECD = 805; %	Standard codes.    ENABLECD = 1001;  PAUSECD = 1003;	 % paradigm stop code  STARTCD = 1005;	 % trial has started   FPONCD = 1010;  FPONBLINKCD = 1012;  FPOFFBLINKCD = 1014;  FPDIMCD = 1020;  FPOFFCD = 1025;  REWCD = 1030;  LISTDONECD = 1035;	 % ramp list has completed   RDONECD = 1037;	 % ramp over  RTDONECD = 1039;	 % ramp timing sequence completed  STIMCD = 1100;  STOFFCD = 1101;  STONBLINKCD = 1120;	 % stim blink  STOFFBLINKCD = 1122;	 % stim blink off  PHIONCD = 1125;	 % phi blink on  PHISTARTCD = 1127;	 % phi sequence started  PHIENDCD = 1129;	 % phi sequence ended   BASECD = 1500;	 % Code that can be modified by an				 % action return.  Leave with 300 consecutive				 % unused numbers.        