amax = 6amin = 0a0 = 3;ntrials = 1000a = nans(ntrials,1);a(1) = a0;c(1) = rand < .5c = rand(ntrials,1)<.5; for i = 2:ntrials  if c(i-1) == 1    % previous trial correct. make it harder (lower a)    a(i) = max([a(i-1)-1 amin]);  else    a(i) = min([a(i-1)+1 amax]);  end    % might be fun to say that   if a(i) == amin    % always wrong    c(i) = 0;  elseif a(i) == amax    % always right    c(i) = 1;  else    c(i) = rand < .5;  endendplot(a)avect = unique(a)p = nan * avectfor i = 1:length(avect)  L = a == avect(i);  p(i) = sum(c(L)) / sum(L) ;endp