function fill_difference(x,y1,y2,C)% fill_difference(x,y1,y2,C)% fills area between two functions Y1(X) and Y2(X) using color C.% C should be 'r', 'g', 'c', etc.%% M Shadlen 9/94, based on Dennis Brown's Q&A in matlab digest%[m n] = size(y1);if m ~= 1  if n ~= 1    sprintf('Error: fill_difference.m, functions must be vectors')    exit;  else    y1 = y1';  endend[m n] = size(y2);if m ~= 1  if n ~= 1    sprintf('Error: fill_difference.m, functions must be vectors')    exit;  else    y2 = y2';  endendif size(x,1) ~= 1  if size(x,2) ~= 1    sprintf('Error: fill_difference.m, x must be vector')  else    x = x'; 				% x is a column vector. we want row  endendfill([x fliplr(x)], [y1 y2], C)