function h = plotbar(x,y,height)%PLOTBAR Plot two vectors simulating a '|' linestyle%     h = plotbar(x,y) returns a handle to the line created.%     h = plotbar(x,y,height) specifies the bar height% 4/16/97 mns added height argumentx = x(:);y = y(:);if nargin < 3  height = ((max(y)-min(y))/length(unique(y)));endnewx = zeros(1,length(x)*3);newx(1:3:end) = x;newx(2:3:end) = x;newx(3:3:end) = nan;newy = zeros(1,length(y)*3);newy(1:3:end) = y;newy(2:3:end) = y+height;newy(3:3:end) = nan;h = plot(newx,newy);