function err = pvtfitfun(q)% used by pvtfitfun.  Does probit with abbot's rule to find a bias and a% standard deviation.  See pvtgausfit.mglobal Data t = Data(:,1);p = Data(:,2);n = Data(:,3);z = q(1) +  (1-q(1)) * normcdf(t,0,q(2));% z = 1 - .5 * exp( -(x/q(1)).^q(2) );llik = n .* p .* log(z) +  n .* (1-p) .* log(1-z);% for moment, just return the square error% err = norm(z-y);err = -sum(llik);