function [err,g] = q2err(q)% q2err error function for quick fit w/ base 2%global Data PlothandleTINY = 1e-100;x = Data(:,1);y = Data(:,2);n = Data(:,3);z = 1 - .5 * 2.^( -(x/q(1)).^q(2) );z = z - (z > .999999)*TINY + (z < .0000001)*TINY; llik = n .* y .* log(z) +  n .* (1-y) .* log(1-z);% for moment, just return the square error% err = norm(z-y);err = -sum(llik);g = [-q(1) -q(2)];