function lst = readlist(fname)% readlist(fname) reads a list of ascii strings in file fname% lst{i} contains the ith string% char(lst{i}) can be used to return a string suitable for strcmp% 1/12/98 mns adopted it from Tiffany Lee's tech support msgfid=fopen(fname);count=1;while 1      eval(['lst{' num2str(count) '}=fgetl(fid);']);      if ~isstr(lst{count}), break, end      % disp(lst);   count=count+1;   endfclose(fid);